-- Which counties belong to the states of Colorado, New Hampshire or New York--------------------------------------------------------- Colorado--------------------------------------------------------- Colorado - Filterselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'Colorado'   and sdo_filter (c.geom, s.geom) = 'TRUE';-- Colorado - anyinteractselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'Colorado'   and sdo_anyinteract (c.geom, s.geom) = 'TRUE';-- Colorado - insideselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'Colorado'   and sdo_inside (c.geom, s.geom) = 'TRUE';-- Colorado - inside + coveredbyselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'Colorado'   and sdo_relate (c.geom, s.geom, 'mask=inside+coveredby') = 'TRUE';--------------------------------------------------------- New Hampshire--------------------------------------------------------- New Hampshire - Filterselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New Hampshire'   and sdo_filter (c.geom, s.geom) = 'TRUE';-- New Hampshire - anyinteractselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New Hampshire'   and sdo_anyinteract (c.geom, s.geom) = 'TRUE';-- New Hampshire - insideselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New Hampshire'   and sdo_inside (c.geom, s.geom) = 'TRUE';-- New Hampshire - inside + coveredbyselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New Hampshire'   and sdo_relate (c.geom, s.geom, 'mask=inside+coveredby') = 'TRUE';--------------------------------------------------------- New York--------------------------------------------------------- New York - Filterselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and sdo_filter (c.geom, s.geom) = 'TRUE';-- New York  - anyinteractselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and sdo_anyinteract (c.geom, s.geom) = 'TRUE';-- New York - insideselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and sdo_inside (c.geom, s.geom) = 'TRUE';-- New York - inside + coveredbyselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and sdo_relate (c.geom, s.geom, 'mask=inside+coveredby') = 'TRUE';-- --------------------------------------------------------------- PROJECTED-- --------------------------------------------------------------- Colorado - inside + coveredbyselect county, c.state_abrv from us_states_p s,      us_counties_p c where s.state = 'Colorado'   and sdo_relate (c.geom, s.geom, 'mask=inside+coveredby') = 'TRUE';-- New Hampshire - inside + coveredbyselect county, c.state_abrv from us_states_p s,      us_counties_p c where s.state = 'New Hampshire'   and sdo_relate (c.geom, s.geom, 'mask=inside+coveredby') = 'TRUE';-- New York - inside + coveredbyselect county, c.state_abrv from us_states_p s,      us_counties_p c where s.state = 'New York'   and sdo_relate (c.geom, s.geom, 'mask=inside+coveredby') = 'TRUE';-- --------------------------------------------------------------- ALTERNATIVES-- -------------------------------------------------------------select county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and sdo_inside (c.geom, s.geom) = 'TRUE'union allselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and sdo_coveredby (c.geom, s.geom) = 'TRUE';select county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and (sdo_inside (c.geom, s.geom) = 'TRUE' or sdo_coveredby (c.geom, s.geom) = 'TRUE');-- --------------------------------------------------------------- RELATIONAL-- --------------------------------------------------------------- Colorado - relationalselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'Colorado'   and s.state_abrv = c.state_abrv;-- New Hampshire - relationalselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New Hampshire'   and s.state_abrv = c.state_abrv;-- New York - relationalselect county, c.state_abrv from us_states s,      us_counties c where s.state = 'New York'   and s.state_abrv = c.state_abrv;
