-- Combine a LIKE and spatial search-- This uses only the index on NAME (in 12c: uses both)SELECT p.id, p.name, p.fcc  FROM us_parks p, us_cities c WHERE p.name like 'Hill%'   AND c.city = 'Dallas'   AND sdo_within_distance (         p.geom, c.location, 'distance=2000 unit=km'       ) = 'TRUE';-- This uses only the spatial indexSELECT p.id, p.name, p.fcc  FROM us_parks p, us_cities c WHERE p.name like '%Mine%'   AND c.city = 'Dallas'   AND sdo_within_distance (         p.geom, c.location, 'distance=2000 unit=km'       ) = 'TRUE';-- This fails if hint is not used-- ORA-13249: SDO_NN cannot be evaluated without using indexSELECT /*+ no_index (p us_parks_name) */ p.id, p.name, p.fcc  FROM us_parks p, us_cities c WHERE p.name like 'Hill%'   AND c.city = 'Dallas'   AND sdo_nn (         p.geom, c.location       ) = 'TRUE'   AND rownum <= 5;-- Create a text index on parks create index us_parks_tx on us_parks (name)   indextype is ctxsys.context;-- Simple word searchSELECT p.id, p.name, p.fcc  FROM us_parks p, us_cities c WHERE contains (p.name,'mine') > 0   AND c.city = 'Dallas'   AND sdo_within_distance (         p.geom, c.location, 'distance=2000 unit=km'       ) = 'TRUE';-- Multiple words search with fuzzy searchSELECT p.id, p.name, p.fcc  FROM us_parks p, us_cities c WHERE contains (p.name, 'mine or ?well') > 0   AND c.city = 'Dallas'   AND sdo_within_distance (         p.geom, c.location, 'distance=2000 unit=km'       ) = 'TRUE';
